import React, { Component } from "react";
import Header from "./components/header";
import Navbar from "./components/navbar";
import Footer from "./components/footer";
import Geez from "./components/geez";
import json from "./geezNums.json";
import _ from 'underscore';


class App extends Component {
    state = {
        count: 0,
        topScore: 0,
        clickedArray: [],
        message: "click on a Ge'ez number to start!"
    };
    
    constructor(props) {
        super(props);
        this.state = {count: 0};
    
        // This binding is necessary to make `this` work in the callback
        this.handleIncrement = this.handleIncrement.bind(this);
    }
    handleIncrement() {
        // We always use the setState method to update a component's state
        this.setState({ count: this.state.count + 1 });
        // alert(this.state.count)
    }

    clickPicture = cickedId => {
        // Arrange the pictures in a random manner
        // const shuffledArray = this.shuffleArray(cards);
        // this.setState({ cards: shuffledArray });
        // if clicked an image already clicked set this.state.score = 0; empty clickeadArray, end of if block
        if (this.state.clickedArray.includes(cickedId)) {
          this.setState({
            count: 0,
            clickedArray: [],
            message: "Oops!! Game Over. Restart page or Click an image to start again!"
          });
        } else {
          this.setState({
            clickedArray: this.state.clickedArray.concat([cickedId]),
            count: this.state.count + 1,
            message: "YAY!!"
          });
        }
        // set topscore = score if score>topscore.
        if (this.state.count > this.state.topScore) {
          this.setState({ topScore: this.state.count });
        }
    };
    
//  setState for the geez
    render() {
        var Cards = [];
        // loops through the geezNums.json and renders images
        for(let index=0;index< json.length ;index++){
            Cards
            .push(<Geez image={json[index].image} name={json[index].name} label={json[index].label} onClick={this.handleIncrement} key={json[index].id}/>)
        }
        //using underscore.js for the shuffle functionality to shuffle the cards everytime on of them gets clicked
        Cards = _.shuffle(Cards)
        
        return (
            
            <React.Fragment>
                <Navbar>
                {/* this sets the props object for count so that the navbar component gets props. (any) word is needed as well or there won't be a number! */}
                <div>
                    <p>Key={this.state.count},Key={this.state.message} ,Key={this.state.topScore}</p>
                    
                </div>
                
                </Navbar>
                <Header />  
                {/* the main tag keeps the images together and formats it in a way where they maintain their proportions */}
                <main className="container">
                    {Cards}
                </main>
                <Footer />
            </React.Fragment>
        );
        
    }
}

export default App;
